            $establishment->address = $row['address'];
            $establishment->city_id = $row['city_id'];
            $establishment->type_id = $row['type_id'];
            $establishment->ave_check_id = $row['ave_check_id'];
            $establishment->parking = $row['parking'];
            $establishment->payment = $row['payment'];
